sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var command = request.queryParams.command;
	var userId = request.queryParams.user_id;
	var text = request.queryParams.text.toString();
	var channelId = request.queryParams.channel_id.toString();
	var token = request.queryParams.token.toString();
	var RESOURCE_PATH = "/api/x_832172_integra/leaves/apply";
	var responseText = "";

	// Check if token is included in the query parameter
	if (token !== undefined || token.length !== 0) {
		if (token === gs.getProperty("x_832172_integra.slackVerificationToken")) {
			if (checkCorrectChannel(channelId)) {
				var restMessage = new sn_ws.RESTMessageV2();
				restMessage.setEndpoint('https://dev80684.service-now.com/' + RESOURCE_PATH );
				restMessage.setHttpMethod('POST');

				// Set Authentication
				var user = 'integration.user';
				var password = gs.getProperty("x_832172_integra.integrationUserPassword");
				restMessage.setBasicAuth(user,password);

				var payload = {
					"data": {
						"command": request.queryParams.command,
						"userId": request.queryParams.user_id,
						"text": request.queryParams.text.toString()
					}
				};

				restMessage.setRequestBody(JSON.stringify(payload));

				restMessage.setRequestHeader("Accept","application/json");
				var restResponse = restMessage.execute();
				var jsonResponse = JSON.parse(restResponse.getBody());

				responseText = jsonResponse.text;
			} else {
				responseText = "You can't use the entered command on this channel.";
			}
		} else {
			responseText = "You're trying to access an invalid endpoint.";
		}
	} else {
		responseText = "You're trying to access an invalid endpoint.";
	}

	var writer = response.getStreamWriter();
	var hdrs = {};

	hdrs['Content-Type'] = 'application/json';
	response.setStatus(200);
    response.setHeaders(hdrs);

	writer.writeString("{\"response_type\":\"ephemeral\",\"text\":\""+responseText+"\"}");
})(request, response);

function checkCorrectChannel (channelId) {
	var channelIdProperty = gs.getProperty("x_832172_integra.channelId");

	if (channelIdProperty === channelId) {
		return true;
	}

	return false;
}
